import React, { Component } from "react";
import { Button, FormGroup, Input, Table } from "reactstrap";
import * as XLSX from 'xlsx';
import './css/upload.css';

export default class Upload extends Component {
    state = {
        xlsxData: [],
        fileName: '',
        pageIndexes: 0,
        singleXLSX: []
    };

    styleSheets = {
        uploadButton: {
            margin: 10
        },
        cleanButton: {
            margin: 10
        },
        buttons: {
            width: 200,
            height: 60,
            position: "absolute",
            margin:"auto",
            inset : 0,
            top : 650
        },
        button: {
            background: "transparent",
            color : "#333",
            border: "none",
             

        },
        buttonSpan : {
            fontSize : 30,
            padding : "0px 20px 0px 20px"
        }
    }



    setNextXLSX = () => {
        const { pageIndexes, xlsxData } = this.state;
        const nextPageIndex = pageIndexes + 10;

        if (nextPageIndex < xlsxData.length) {
            const tempArray = xlsxData.slice(nextPageIndex, nextPageIndex + 10);
            this.setState({
                pageIndexes: nextPageIndex,
                singleXLSX: tempArray,
                backButtonState: nextPageIndex < 10,
                nextButtonState: nextPageIndex + 10 >= xlsxData.length,
            });
        }
    };






    handleFile = (event) => {
        const file = event.target.files[0];
        if (file.name.includes(".xlsx"))
            this.convertExcelToJson(file);
        else {
            alert("Sadece xlsx uzantılı dosyalar listelenir")
        }
    };



    clearInput = () => {
        document.querySelector(".fileInput").value = ""
        this.setState({ xlsxData: [] })
    }








    pushDataToTable = data => {
        const { paths, url } = this.props
        const fetchUrl = url + paths.insert
        data.forEach((e) => {
            let body = {
                LanguageCode: e.LanguageCode,
                PageName: e.PageName,
                ResourceCode: e.ResourceCode,
                Resource: e.Resource
            }


            let options = {
                method: 'POST',
                headers: {
                    "Content-type": "Application/json"
                },
                body: JSON.stringify(body)
            }




            fetch(fetchUrl, options)
                .then(resp => resp.json())
                .then(resp => console.log(resp))
        })
    }

    convertExcelToJson = (file) => {


        try {
            const reader = new FileReader();


            reader.onload = (event) => {
                let data = event.target.result;
                let workbook = XLSX.read(data, { type: 'binary' });
                let tempData = [];
                workbook.SheetNames.forEach((entry) => {
                    let worksheet = workbook.Sheets[entry];
                    let xlsxData = XLSX.utils.sheet_to_json(worksheet, { raw: false });
                    tempData.push(xlsxData);
                    console.log(xlsxData);
                });
                this.setState({ xlsxData: tempData[0] });
            };
            reader.readAsBinaryString(file);
        }
        catch (err) {
            alert("Yuklenen dosya xlsx olmalı")
        }
    };

    render() {
        const { fileName, xlsxData } = this.state;
        const { uploadButton, cleanButton, buttons, button , buttonSpan } = this.styleSheets

        return (
            <div className="uploadMain" hidden={this.props.isUpload}>
                <span
                    className="material-symbols-outlined close"
                    onClick={() => { this.props.setUploadState(!this.props.isUpload) }}
                >
                    close
                </span>
                <div className="uploadContainer">
                    <FormGroup>
                        <Input
                            className="fileInput"
                            type="file"
                            id="fileInput"
                            onChange={this.handleFile}
                            accept=".xlsx"
                        />
                    </FormGroup>
                    {xlsxData.length > 0 && (
                        <div className="previewTable">
                            <Table>
                                <thead>
                                    <tr>
                                        {Object.keys(xlsxData[0]).map((e, index) => (
                                            <th key={index} className="xlsxTableHeaders">{e}</th>
                                        ))}
                                    </tr>
                                </thead>
                                <tbody>
                                    {Object.values(xlsxData).map((e, index) => (
                                        <tr key={index} style={{ height: 100, wordBreak: "break-all" }}>
                                            {Object.values(e).map((j, key) => (
                                                <td
                                                    key={key}
                                                    className="uploadTableItem"
                                                >{j}</td>

                                            ))}
                                        </tr>
                                    ))}
                                </tbody>
                            </Table>
                            <div className="pushButtons">
                                <Button
                                    color="success"
                                    onClick={() => { this.pushDataToTable(xlsxData) }}
                                    style={uploadButton}
                                >Upload</Button>
                                <Button
                                    color="dark"
                                    onClick={this.clearInput}
                                    style={cleanButton}
                                >Clear</Button>
                            </div>
                            <span className="logAnnouncer">
                                {xlsxData.length} adet kayıt girildi
                            </span>
                        </div>
                    )}
                    <div style={buttons}>
                        <Button
                            style={button}
                        ><span className="material-symbols-outlined" style={buttonSpan}>arrow_left_alt</span></Button>
                        <Button
                            style={button}
                        ><span className="material-symbols-outlined" style={buttonSpan}>arrow_right_alt</span></Button>
                    </div>
                </div>
            </div>
        );
    }
}
