import React, { Component, useEffect, useState } from 'react'
import { Button, Container } from 'reactstrap'
import './css/updateScreen.css'
function Update(props){

    let [state,setState] = useState(props.isSetUpdate)

    function setStatus(newState){
        setState(newState)
    }


    return (
     <div
        className='mainContainer'
        hidden={state}
     >
        <button
            className='closeButton'
            onClick={()=>setStatus(props.isSetUpdate)}
        >
            <span 
                className='material-symbols-outlined closeButtonIcon'

            >close</span>
        </button>


     </div>
    )

}



export default Update
