import React, { Component } from 'react'
import { Container,Dropdown,DropdownItem,DropdownMenu,DropdownToggle,Input } from 'reactstrap'
import './css/searcher.css'
export default class searcher extends Component {
  
    
    state = {
        inputValue : '',
        logs : this.props.logs,
        foundCustomers : [],
        dropDownIsOpen : false,
        defaultFilterText : 'Filter by <option>',
        sections : ["Id","LanguageCode","PageName","ResourceCode","Resource"]
    }
    

    handleInput =  async (value)=>{
       
        this.props.searchCustomers(value.target.value)
   } 

    hanldeDefaultFilterText = index =>{
      this.setState({defaultFilterText : this.state.sections[index]})
    }
    

    openFilterList = () =>{
      this.setState({dropDownIsOpen : !this.state.dropDownIsOpen})
    } 
   

  
    render() {
       

        const { inputValue } = this.state



    
    return (
      <div>
        <Container className='main'>
        <h2>Search</h2>
        <div className='searcher'>
            <Input onChange={this.handleInput} className='searchInput'></Input>
            <p hidden={this.props.notFound} >Aradığınız isim bulunamadı</p>
        </div>
        </Container>
        <div className='filterSections'>
          <Dropdown isOpen={this.state.dropDownIsOpen} toggle={this.openFilterList}>
            <DropdownToggle color={"white"} className="toggle">{this.state.defaultFilterText}</DropdownToggle>
            <DropdownMenu>
              {this.state.sections.map((e,index)=>(
                <DropdownItem 
                  key={index}
                  onClick={()=>{
                    this.hanldeDefaultFilterText(index)
                  }}
                >{e}</DropdownItem>
              ))}
            </DropdownMenu>
            
          </Dropdown>
        </div>

      </div>
      
    )
  }
}
