import React, { Component } from 'react'
import { Button } from 'reactstrap'
import * as XLSX from 'xlsx';


export default class download extends Component {

    styleSheets = {
        mainContainer: {
            width: 100,
            height: 50,
            backgroundColor: "#fff",
            position: "absolute",
            left: 120,
            top:52.5
        },
        downloadButton: {
            width : 50,
            height: 40,
            display : "flex",
            justifyContent : "center",
            alignItems : "center"
        },
        buttonSpan : {
            display : "flex",
            justifyContent : "center",
            alignItems : "center"
        }
    }


    convertJsonToExcel = data =>{



        const columnHeaders = Object.keys(data[0]);
        const columnWidths = columnHeaders.map(header => ({wch : header.length + 20}))


        
        worksheet['!cols'] = columnWidths;

        const workbook = XLSX.utils.book_new();

        XLSX.utils.book_append_sheet(workbook,worksheet,"tables");
        XLSX.writeFile(workbook,"logs.xlsx");
    }



    render() {

        const { mainContainer,downloadButton,buttonSpan} = this.styleSheets;
        const { logs } = this.props
        return (
            <div
                style={mainContainer}>
                <Button

                    color='secondary'
                    style={downloadButton}
                    title="Download table"
                    onClick={()=>this.convertJsonToExcel(logs)}
                ><span className='material-symbols-outlined' style={buttonSpan}>download</span></Button>
            </div>
        )
    }
}
