import React, { Component} from 'react';
import {Table,ListGroup,ListGroupItem,Button} from 'reactstrap';
import './App.css';
import Update from './components/updateScreen'


export default class App extends Component {


  state = {
    customers : [],
    isActive : false,
    listMethods : ["List by id","List by salary"],
    currentMethod : "0",
    isUpdate : true
  }

  updateIsSetUpdate = (newState) => {
    this.setState({ isUpdate: newState });
  };


  sortCustomersBySalary = (sortParameter) =>{
    const { customers } = this.state;
    const sortedCustomers = [...customers]
    switch (sortParameter) {
      case 1 : {
        sortedCustomers.sort((a,b)=>a.id-b.id)
        this.setState({customers:sortedCustomers})
        break
      }
      case 2 : {
        sortedCustomers.sort((a,b)=>a.salary-b.salary)
        this.setState({customers:sortedCustomers})
        break
      }
      default : {
        break
      }
    }
  }


  getCustomers = async () =>{
    await fetch("http://localhost:4444/customers").then(resp=>resp.json()).then(resp=>this.setState({customers : resp}))
  }


  componentDidMount(){
    this.getCustomers()
  }

  setCurrentListMethod = method =>{
      this.setState({currentMethod : method});
  }

  setUpdateState = () =>{
    this.setState({ isUpdate: !this.state.isUpdate })
  }


  render() {
    const {customers,listMethods,isUpdate} = this.state

    return (
      <div>
        {customers.length > 0 && (
          <div >
            <Table className='table'>
              <thead>
                <tr>
                  {Object.keys(customers[0]).map((e,index)=>(
                    <th key={index}>
                      {e}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {customers.map((e,index)=>(
                  <tr key={e.id}>
                    <td>{e.id}</td>
                    <td>{e.name}</td>
                    <td>{e.country}</td>
                    <td>{e.salary}</td>
                    <td>{e.profession}</td>
                    <td>
                      <Button
                        color='success'
                        style={{width:100}}
                        onClick={()=>{this.setUpdateState();console.log(isUpdate)}}
                      >update</Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
            <ListGroup className='listBar'>
              <h2 style={{textAlign:"center",padding:25}}>List Methods</h2>
              {listMethods.map((e,index)=>(
                <ListGroupItem
                tag="a"
                className='listSection' 
                key={index} 
                onClick={()=>{this.setCurrentListMethod(index);this.sortCustomersBySalary(index + 1)}} 
                active={this.state.currentMethod === index?true:false} 
                >{e}</ListGroupItem>
              ))}
            </ListGroup>

          </div>


        )}
        <Update 
         isSetUpdate={isUpdate}
         updateIsSetUpdate={this.updateIsSetUpdate}

        ></Update>
      </div>
    )
  }
}
