import React, { Component } from 'react'
import { Input, InputGroup, InputGroupText, Button } from 'reactstrap'

export default class insertSingle extends Component {

    styleSheets = {
        main: {
            width: "100vw",
            height: "100vh",
            backgroundColor: "rgba(0,0,0,0.2)",
            position: "fixed",
            zIndex: 2
        },
        container: {
            width: 600,
            height: 400,
            backgroundColor: "#fff",
            position: "absolute",
            inset: 0,
            margin: "auto",
            display: "grid",
            paddingLeft: "5%",
            alignItems: "center",
            borderRadius: 5

        },
        closeButton: {
            background: "transparent",
            border: "none",
            width: 30,
            height: 30,
            color: "#fff",
            position: "absolute",
            top: 30,
            right: 50
        },
        inputs: {
            width: "90%"
        },
        button: {
            width: "60%",
            marginLeft: "15%",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "rgb(255, 165, 0)",
            border: "none"
        },
        label: {
            width: 120,
            backgroundColor: "#333",
            color: "white",

            fontSize: 14
        }

    }


    setHoverColors = event =>{
        event.target.style.backgroundColor = "rgb(194, 127, 4)"
        
    }
    
    

    render() {
        const { main, container, closeButton, inputs, button, label } = this.styleSheets
        const { isInsert, setInsertState } = this.props


        return (
            <div style={main} hidden={!isInsert}>
                <button
                    style={closeButton}
                    onClick={() => {
                        setInsertState(!isInsert)
                    }}
                ><span className='material-symbols-outlined'>close</span></button>
                <div style={container}>
                    <form style={container}>
                        <InputGroup style={inputs}>
                            <InputGroupText style={label}>LanguageCode</InputGroupText>
                            <Input required />
                        </InputGroup>
                        <InputGroup style={inputs}>
                            <InputGroupText style={label}>PageName</InputGroupText>
                            <Input required />
                        </InputGroup>
                        <InputGroup style={inputs}>
                            <InputGroupText style={label}>ResourceCode</InputGroupText>
                            <Input required />
                        </InputGroup>
                        <InputGroup style={inputs}>
                            <InputGroupText style={label}>Resource</InputGroupText>
                            <Input required />
                        </InputGroup>
                        <Button
                            onMouseEnter={(e) => {this.setHoverColors(e)}}
                            onMouseLeave={(e) => { e.target.style.backgroundColor = "rgb(255, 165, 0)" }}
                            style={button}
                        ><span className='material-symbols-outlined'>add</span></Button>

                    </form>



                </div>
            </div>
        )
    }
}
