import React, { Component } from 'react';
import { Table, Button } from 'reactstrap';


//CSS files
import './App.css';

//Components
import DeleteValidation from './components/deleteValidation'
import Update from './components/update'
import Searcher from './components/searcher'
import Upload from './components/upload'
import Download from './components/download'
import Insert from './components/insertSingle'



export default class App extends Component {


  state = {
    logs: [],
    allLogs: [],
    lastId: null,
    isUpdate: true,
    notFound: true,
    isDelete: false,
    isUpload: true,
    isInsert: false,
    nextButtonState: false,
    selectedLog: [],
    currentPageLastId: null,
    url: "http://localhost:4444",
    paths: {
      update: "/update/",
      delete: "/delete/",
      getAllCustomers: "/logs",
      insert: "/insert"
    },
    page: 1,
    currentPageLogs: [],
    convertedData: [],
    hoverState: true,
    direction: true
  }


  styleSheets = {
    insertLog: {
      width: 50,
      height: 40.5,
      position: "absolute",
      border: "none",
      left: 190,
      top: 52,
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      color: "white"
    },
    nextButton: {
      position: "absolute",
      width: "5vw",
      height: "2.5vw",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      background: "transparent",
      color: "#333",
      fontWeight: 700,
      border: "none",
      right: "40vw",
      fontSize: "30px"
    },
    nextButtonSpan: {
      left: "47.5vw",
      fontSize: "30px",
      fontWeight: 700,
      color: "#333"
    },
    backButton: {
      position: "absolute",
      width: "5vw",
      height: "2.5vw",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      background: "transparent",
      color: "#333",
      fontWeight: 700,
      border: "none",
      left: "40vw",
      fontSize: "30px"
    },
    backButtonSpan: {
      left: "47.5vw",
      fontSize: "30px",
      fontWeight: 700,
      color: "#333"
    }
  }






  resetNotFoundState = () => {
    this.setState({ notFound: true })
  }



  setInsertState = state => {
    this.setState({ isInsert: state })
  }


  getConvertedData = data => {
    this.setState({ convertedData: data })
  }


  searchCustomers = (input, sortParam) => {
    if (input.length > 0) {
      const { allLogs } = this.state
      const unHandledLogs = [...allLogs];
      let sortedLogs = []
      switch (sortParam) {
        case 0: {

          sortedLogs = unHandledLogs.filter((a) => `${a.Id}`.includes(input))
          break;
        }
        case 1: {
          sortedLogs = unHandledLogs.filter((a) => a.LanguageCode.toLowerCase().includes(input.toLowerCase()))
          break;
        }
        case 2: {
          sortedLogs = unHandledLogs.filter((a) => a.PageName.toLowerCase().includes(input.toLowerCase()))
          break;
        }
        case 3: {
          sortedLogs = unHandledLogs.filter((a) => a.ResourceCode.toLowerCase().includes(input.toLowerCase()))
          break;
        }
        case 4: {
          sortedLogs = unHandledLogs.filter((a) => a.Resource.toLowerCase().includes(input.toLowerCase()))
          break;
        }
      }
      if (sortedLogs.length > 0) {
        this.setState({ logs: sortedLogs })
        this.setState({ notFound: true })
      }
      else {
        this.getCustomers()
        this.setState({ notFound: false })
      }
    }
    else {
      this.getCustomers();
    }
  }



