    let maxPage = Math.ceil(lastId / 10)
    console.log(maxPage)
    this.setState({ page: page + 1 }, () => {
      console.log(allLogs[lastId-1].Id,logs[logs.length - 1].Id)
      if (logs[logs.length - 1].Id !== allLogs[lastId-1].Id){
        this.setPageButtonDirection(2)
      }
      else{
        this.setState({nextButtonState : true})
      }
    })
  }




