import React, { Component } from 'react'
import { ListGroup,ListGroupItem } from 'reactstrap';

export default class listMethods extends Component {

    state = {
        customers : this.props.customers,
        isActive : false,
        listMethods : ["List by id","List by salary","List by country"],
        currentMethod : "0",
        isUpdate : true,
        selectedUser : []
      }


  render() {
    const {setCurrentListMethod,sortCustomersBySalary} = this.props
    const { listMethods } = this.state
    return (
        <ListGroup className='listBar'>
        <h2 style={{textAlign:"center",padding:25}}>List Methods</h2>
        {listMethods.map((e,index)=>(
          <ListGroupItem
          tag="a"
          className='listSection' 
          key={index} 
          onClick={()=>{setCurrentListMethod(index);this.sortCustomers(index + 1)}} 
          active={this.state.currentMethod === index ? true:false} 
          >{e}</ListGroupItem>
        ))}
      </ListGroup>
    )
  }
}
