import React, { Component } from 'react'
import { Input, InputGroup, InputGroupText, Button } from 'reactstrap'


import * as insert from './upload'

export default class insertSingle extends Component {


    state = {
        log: [],
        dataTypes: ["LanguageCode", "PageName", "ResourceCode", "Resource"]
    }




    styleSheets = {
        main: {
            width: "100vw",
            height: "100vh",
            backgroundColor: "rgba(0,0,0,0.2)",
            position: "fixed",
            zIndex: 2
        },
        container: {
            width: "35vw",
            height: 400,
            backgroundColor: "#fff",
            position: "absolute",
            inset: 0,
            margin: "auto",
            display: "grid",
            paddingLeft: "5%",
            alignItems: "center",
            borderRadius: 5,
            left: "10vw"

        },
        closeButton: {
            background: "transparent",
            border: "none",
            width: 30,
            height: 30,
            color: "#fff",
            position: "absolute",
            top: 30,
            right: 50
        },
        inputs: {
            width: "90%"
        },
        button: {
            width: "60%",
            marginLeft: "15%",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            border: "none"
        },
        label: {
            width: 120,
            backgroundColor: "#333",
            color: "white",

            fontSize: 14
        },
        dataValidationContainer: {
            
            display: "grid",
            width: "25vw",
            height: 400,
            backgroundColor: "#fff",
            position: "absolute",
            
            top: 173,
            left: "13vw"
            
        },
        readInputs : {
            width : "90%",
            height : 80,
            margin: "auto"
        }

    }   


    clearInputs = () =>{
        const inputs= document.querySelectorAll(".Inputs")

        inputs.forEach(e=>e.value = "")

    }



    render() {
        const { main, container, closeButton, inputs, button, label, dataValidationContainer ,readInputs} = this.styleSheets
        const { isInsert, setInsertState } = this.props


        return (
            <div style={main} hidden={!isInsert}>
                <button
                    style={closeButton}
                    onClick={() => {
                        this.clearInputs()
                        setInsertState(!isInsert)
                    }}
                ><span className='material-symbols-outlined'>close</span></button>
                <div style={container}>
                    <form style={container}>
                        {this.state.dataTypes.map((e, index) => (
                            <InputGroup style={inputs} key={index}>
                                <InputGroupText style={label}>{e}</InputGroupText>
                                <Input
                                    className='Inputs'
                                    onChange={(e) => this.setState((prevState) =>{
                                        let newData = [...prevState.log]
                                        newData[index] = e.target.value;
                                        this.setState({log : newData})
                                    })}
                                    required />
                            </InputGroup>
                        ))}
                        <Button
                            type='submit'
                            style={button}
                            color="success"
                            onClick={() => { }}
                        >
                            <span className='material-symbols-outlined'>add</span></Button>

                    </form>

                </div>
                <div style={dataValidationContainer}>
                    {this.state.log.length > 0 && (
                        this.state.log.map((e,index)=>(
                            (e.length > 0 && (
                                <Input readOnly={true} key={index} value={this.state.dataTypes[index] + " : " + e} style={readInputs}></Input>
                            ))
                            
                        ))
                    )}
                </div>
            </div>
        )
    }
}
