import React, { Component } from "react";
import { Button, FormGroup, Input, Table } from "reactstrap";
import * as XLSX from 'xlsx';
import './css/upload.css';

export default class Upload extends Component {
    state = {
        xlsxData: [],
        fileName: '',
        xlsxIndexes: 0,
        singleXLSX: [],
        backButtonState: true,
        nextButtonState: false
    };

    styleSheets = {
        uploadButton: {
            margin: "0px 10px 10px 10px",


        },
        cleanButton: {
            margin: "0px 10px 10px 10px",

        },
        buttons: {
            width: "10vw",
            height: 60,
            position: "absolute",
            left: "40vw",
            display:"flex"
            
        },
        button: {
            background: "transparent",
            color: "#333",
            border: "none",


        },
        buttonSpan: {
            fontSize: 30,
            padding: "0px 20px 0px 20px"
        },
        uploadMain: {
            width: "100vw",
            height: "100vh",
            backgroundColor: "#fff",
            position: "fixed",
            zIndex: 1,
            overflow: "scroll",
            overflowX: "hidden",
            display: "grid",
            justifyContent: "center",
        },
        fileInput: {
            position: "absolute",
            top: 10,
            left: 0,
            width: "30vw",
            left: "35vw",
            height: 35
        },
        closeButton: {
            position: "absolute",
            right: 30,
            fontSize: 30,
            top: 10,
            cursor: "pointer"
        },
        previewTable: {
            position: "absolute",
            top: 200,
            left: "5vw",
            width: "80vw",
        },
        transactionButtons: {
            position: "absolute",
            display: "flex",
            alignItems: "center",
            width: 300,
            height: 100,
            right: -250
        },
        announcer: {
            position: "absolute",
            display: "flex",
            alignItems: "center",
            width: 300,
            height: 100,

        },
        nextButton:{
            marginLeft : "5vw",
            background : "transparent",
            color : "#333",
            border : "none"

        },
        backButton:{
            marginRight : "20px",
            background : "transparent",
            color : "#333",
            border : "none"
        }
    }


    setNextXLSX = () => {
        const { xlsxData, xlsxIndexes } = this.state

        const nextPageXLSX = xlsxIndexes + 10;

        if (nextPageXLSX < xlsxData.length) {
            const tempArray = xlsxData.slice(nextPageXLSX, nextPageXLSX + 10)
            this.setState({
                xlsxIndexes: nextPageXLSX,
                singleXLSX: tempArray,
                backButtonState: nextPageXLSX < 10,
                nextButtonState: nextPageXLSX + 10 >= xlsxData.length
            }, () => console.log(this.state.singleXLSX))
        }
    }







    handleFile = (event) => {
        const file = event.target.files[0];
        if (file.name.includes(".xlsx"))
            this.convertExcelToJson(file);
        else {
            alert("Sadece xlsx uzantılı dosyalar listelenir")
        }
    };



    clearInput = () => {
        document.querySelector(".fileInput").value = ""
        this.setState({ xlsxData: [], singleXLSX: [] })
    }








    pushDataToTable = data => {
        const { paths, url } = this.props
        const fetchUrl = url + paths.insert
        data.forEach((e) => {
            let body = {
                LanguageCode: e.LanguageCode,
                PageName: e.PageName,
                ResourceCode: e.ResourceCode,
                Resource: e.Resource
            }


            let options = {
                method: 'POST',
                headers: {
                    "Content-type": "Application/json"
                },
                body: JSON.stringify(body)
            }




            fetch(fetchUrl, options)
                .then(resp => resp.json())
                .then(resp => console.log(resp))
        })
    }

    convertExcelToJson = (file) => {


        try {
            const reader = new FileReader();


            reader.onload = (event) => {
                let data = event.target.result;
                let workbook = XLSX.read(data, { type: 'binary' });
                let tempData = [];


                workbook.SheetNames.forEach((entry) => {
                    let worksheet = workbook.Sheets[entry];
                    let xlsxData = XLSX.utils.sheet_to_json(worksheet, { raw: false });
                    tempData.push(xlsxData);
                    console.log(xlsxData);
                });



                this.setState({
                    xlsxData: tempData[0],
                }, () => {
                    let tempArray = []
                    for (let i = 0; i < 10; i++) {
                        tempArray.push(this.state.xlsxData[i])
                    }
                    this.setState({ singleXLSX: this.state.xlsxData.slice(0, 10) })

                });
            };
            reader.readAsBinaryString(file);
        }
        catch (err) {
            alert("Yuklenen dosya xlsx olmalı")
        }
    };

    render() {
        const { xlsxData, nextButtonState, backButtonState, singleXLSX } = this.state;
        const {
            uploadButton,
            cleanButton,
            buttons,
            button,
            buttonSpan,
            uploadMain,
            fileInput,
            closeButton,
            previewTable,
            transactionButtons,
            announcer,
            nextButton,
            backButton
        } = this.styleSheets

        return (
            <div style={uploadMain} hidden={this.props.isUpload}>
                <span
                    className="material-symbols-outlined close"
                    onClick={() => { this.props.setUploadState(!this.props.isUpload); this.clearInput() }}
                    style={closeButton}
                >
                    close
                </span>
                <div className="uploadContainer">
                    <FormGroup>
                        <Input
                            className="fileInput"
                            type="file"
                            id="fileInput"
                            onChange={this.handleFile}
                            accept=".xlsx"
                            style={fileInput}
                        />
                    </FormGroup>
                    {singleXLSX.length > 0 && (
                        <div className="previewTable" style={previewTable}>
                            <Table>
                                <thead>
                                    <tr>
                                        {Object.keys(singleXLSX[0]).map((e, index) => (
                                            <th key={index} className="xlsxTableHeaders">{e}</th>
                                        ))}
                                    </tr>
                                </thead>
                                <tbody>
                                    {Object.values(singleXLSX).map((e, index) => (
                                        <tr key={index} style={{ height: 100, wordBreak: "break-all" }}>
                                            {Object.values(e).map((j, key) => (
                                                <td
                                                    key={key}
                                                    className="uploadTableItem"
                                                >{j}</td>

                                            ))}
                                        </tr>
                                    ))}
                                </tbody>
                            </Table>
                            <div className="pushButtons" style={transactionButtons}>
                                <Button
                                    color="success"
                                    onClick={() => { this.pushDataToTable(xlsxData) }}
                                    style={uploadButton}
                                >Upload</Button>
                                <Button
                                    color="dark"
                                    onClick={this.clearInput}
                                    style={cleanButton}
                                >Clear</Button>
                            </div>
                            <span className="logAnnouncer" style={announcer}>
                                {xlsxData.length} adet kayıt girildi
                            </span>
                            <div style={buttons}>
                                <Button
                                    style={backButton}
                                    
                                    hidden={backButtonState}
                                ><span className="material-symbols-outlined" style={buttonSpan}>arrow_left_alt</span></Button>
                                <Button
                                    style={nextButton}
                                    onClick={this.setNextXLSX}
                                    hidden={nextButtonState}
                                ><span className="material-symbols-outlined" style={buttonSpan}>arrow_right_alt</span></Button>
                            </div>
                        </div>

                    )}

                </div>
            </div>
        );
    }
}
