      color: "#333"
    },
    emptyMainContainer: {
      width: "100vw",
      height: "100vh",
      position: "absolute",
      margin: "auto",
      inset: 0
    }
  }






  resetNotFoundState = () => {
    this.setState({ notFound: true })
  }



  setInsertState = state => {
    this.setState({ isInsert: state })
  }





  searchCustomers = (input, sortParam) => {
    if (input.length > 0) {
      const { allLogs } = this.state
      const unHandledLogs = [...allLogs];
      let sortedLogs = []
      switch (sortParam) {
        case 0: {

          sortedLogs = unHandledLogs.filter((a) => `${a.Id}`.includes(input))
          break;
        }
        case 1: {
          sortedLogs = unHandledLogs.filter((a) => a.LanguageCode.toLowerCase().includes(input.toLowerCase()))
          break;
        }
        case 2: {
          sortedLogs = unHandledLogs.filter((a) => a.PageName.toLowerCase().includes(input.toLowerCase()))
          break;
        }
        case 3: {
          sortedLogs = unHandledLogs.filter((a) => a.ResourceCode.toLowerCase().includes(input.toLowerCase()))
          break;
        }
        case 4: {
          sortedLogs = unHandledLogs.filter((a) => a.Resource.toLowerCase().includes(input.toLowerCase()))
          break;
        }
      }
      if (sortedLogs.length > 0) {
        this.setState({ logs: sortedLogs })
        this.setState({ notFound: true })
      }
      else {
        this.getCustomers()
        this.setState({ notFound: false })
      }
    }
    else {
      this.getCustomers();
    }
  }





  removeCustomerFromDB = customer => {
    const { url, paths, logs } = this.state
    let newCustomers = [...logs]
    newCustomers = newCustomers.filter(e => e.Id !== customer.Id)
    this.setState({ logs: newCustomers })
    let fetchUrl = url + paths.delete + customer.Id
    fetch(fetchUrl).then(resp => resp.json()).then(() => { alert("removed successfully") }).then(() => this.getCustomers()).then(() => { if (this.state.logs.length == 0) { this.getFirstLogs() } })

  }

  setDeleteState = state => {
    this.setState({ isDelete: state })
  }

  sortCustomersByResourceCode = (sortParameter) => {
    const { allLogs, pageIndexes } = this.state
    const sortedCustomers = [...allLogs]

    switch (sortParameter) {
      case 1: {
        sortedCustomers.sort((a, b) => a.Id - b.Id)
        this.setState({
          logs: sortedCustomers.slice(pageIndexes, pageIndexes + 10),
          allLogs: sortedCustomers
        })
        break
      }
      case 2: {
        sortedCustomers.sort((a, b) => {
          if (a.LanguageCode < b.LanguageCode) return -1;
          if (a.LanguageCode > b.LanguageCode) return 1;
          return 0;
        });
        this.setState({
          logs: sortedCustomers.slice(pageIndexes, pageIndexes + 10),
          allLogs: sortedCustomers
        })
        break



      }
      case 3: {
        sortedCustomers.sort((a, b) => {
          if (a.PageName < b.PageName) return -1;
          if (a.PageName > b.PageName) return 1;
          return 0;
        });
        this.setState({
          logs: sortedCustomers.slice(pageIndexes, pageIndexes + 10),
          allLogs: sortedCustomers
        })
        break
      }
      case 4: {
        sortedCustomers.sort((a, b) => {
          if (a.ResourceCode.toLowerCase() > b.ResourceCode.toLowerCase()) return 1;
          if (a.ResourceCode.toLowerCase() < b.ResourceCode.toLowerCase()) return -1;
          return 0;
        });
        this.setState({
          logs: sortedCustomers.slice(pageIndexes, pageIndexes + 10),
          allLogs: sortedCustomers
        })
        break
      }
      case 5: {
        sortedCustomers.sort((a, b) => {
          if (a.Resource.toLowerCase() > b.Resource.toLowerCase()) return 1;
          if (a.Resource.toLowerCase() < b.Resource.toLowerCase()) return -1;
          return 0;
        });
        this.setState({
          logs: sortedCustomers.slice(pageIndexes, pageIndexes + 10),
          allLogs: sortedCustomers
        })
        break
      }
      default: {
        break
      }
    }
  }


  setPageButtonDirection = () => {




  }


  getFirstLogs = () => {
    const { allLogs } = this.state;

    let tempArray = [];

    for (let i = 0; i < 10; i++) {
      tempArray.push(allLogs[i])
    }


    this.setState({
      logs: tempArray,
      backButtonState: true,
      nextButtonState: false
    })


  }

  getCustomers = async () => {
    const { url, paths } = this.state
    let fetchUrl = url + paths.getAllCustomers
    await fetch(fetchUrl).then(resp => resp.json()).then(resp => {
      this.setState({ lastIndex: resp.length }, () => {

      })
      this.setState({ allLogs: resp })
    })
  }


  setPageNext = () => {
    const { pageIndexes, allLogs, page } = this.state;
    const nextPageIndex = pageIndexes + 10;

    if (nextPageIndex < allLogs.length) {
      const tempArray = allLogs.slice(nextPageIndex, nextPageIndex + 10);
      this.setState({
        pageIndexes: nextPageIndex,
        page: page + 1,
        logs: tempArray,
        backButtonState: nextPageIndex < 10,
        nextButtonState: nextPageIndex + 10 >= allLogs.length,
      });
    }
  };





  setPageBack = () => {
    const { pageIndexes, allLogs, page } = this.state;
    const prevPageIndex = pageIndexes - 10;

    if (prevPageIndex >= 0) {
      const tempArray = allLogs.slice(prevPageIndex, prevPageIndex + 10);
      this.setState({
        pageIndexes: prevPageIndex,
        page: page - 1,
        logs: tempArray,
        nextButtonState: false,
        backButtonState: prevPageIndex < 10
      });
    }
  };




  async componentDidMount() {
    await this.getCustomers()
    await this.putLogsInTable();

  }
  setCurrentListMethod = element => {
    document.querySelectorAll(".listMethods").forEach(e => e.style.color = "#333")
    element.style.color = "rgb(29, 206, 41)"
  }
  setUpdateState = state => {

    this.setState({ isUpdate: state })
  }


  putLogsInTable = () => {
    const { pageIndexes, allLogs } = this.state;
    let tempArray = [];
    if (allLogs.length > 0) {
      for (var i = pageIndexes; i < pageIndexes + 10; i++) {
        tempArray.push(allLogs[i])
      }
      console.log(allLogs)
    }


    this.setState({ logs: tempArray }, () => {
      this.setState({ pageIndexes: pageIndexes + 10 })
    });
  };


  setUploadState = state => {
    this.setState({ isUpload: state })
  }










  render() {
    const { logs, isUpdate, selectedLog, allLogs } = this.state
    const { insertLog } = this.styleSheets
    return (
      <div>
        {logs.length > 0 && (
          <div>
            <Insert setInsertState={this.setInsertState} isInsert={this.state.isInsert} url={this.state.url} paths={this.state.paths} getLogs={this.getCustomers}></Insert>
            <Download logs={allLogs}></Download>
            <Searcher searchCustomers={this.searchCustomers} logs={this.state.allLogs} notFound={this.state.notFound} reset={this.resetNotFoundState}></Searcher>

            <div>
              <Button
                title='upload files'
                className='uploadButton'
                color='dark'
                onClick={() => {
                  this.setUploadState(!this.state.isUpload)
                }}

              ><span className='material-symbols-outlined'>upload_file</span></Button>
            </div>

            <div>
              <Button
                title='insert single log'
                style={insertLog}
                color="success"
                onClick={() => { this.setInsertState(!this.state.isInsert) }}
              >
                <span className='material-symbols-outlined'>add</span>
              </Button>
            </div>



            <div className='mainContainer' hidden={!this.state.isUpload}>

              <div
                className='table'
              >
                {logs.length > 0 && (
                  <div>
                    <Table>
                      <thead>
                        <tr>
                          {Object.keys(logs[0]).map((e, index) => (
                            <th key={index} className="headerCol">
                              <h5
                                className={`listMethods lm-${index}`}
                                onClick={() => {
                                  this.sortCustomersByResourceCode(index + 1);
                                  this.setCurrentListMethod(document.querySelector(`.lm-${index}`))

                                }}


                              >{e}</h5>
                            </th>
                          ))}
                          <th style={{ textAlign: "center", wIdth: 50, position: "relative", left: "30px" }}><h5>Edit</h5></th>
                          <th style={{ textAlign: "center", wIdth: 50 }}><h5>Delete</h5></th>
                        </tr>
                      </thead>
                      <tbody>
                        {logs.map((e) => (
                          <tr key={e.Id} className='logData'>
                            <td>{e.Id}</td>
                            <td>{e.LanguageCode}</td>
                            <td>{e.PageName}</td>
                            <td>{e.ResourceCode}</td>
                            <td>{e.Resource}</td>
                            <td style={{ width: "3vw", position: "relative", left: "30px" }}>
                              <Button
                                color='dark'
                                style={{ marginLeft: 20 }}
                                onClick={() => {
                                  this.setUpdateState(!isUpdate);
                                  this.setState({ selectedLog: e });

                                }}
                              ><span className='material-symbols-outlined'>edit</span></Button>
                            </td>
                            <td style={{ width: "3vw", position: "relative" }}>
                              <Button
                                color='danger'
                                style={{ marginLeft: 0, }}
                                onClick={() => {
                                  this.setState({ isDelete: !this.state.isDelete })
                                  this.setState({ selectedLog: e })
                                }}
                              ><span className='material-symbols-outlined'>delete</span></Button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </Table>
                    <div>
                      <Button
                        hidden={this.state.nextButtonState}
                        onClick={() => { this.setPageNext() }}
                        style={this.styleSheets.nextButton}
                      >
                        <span className="material-symbols-outlined rightArrow" style={this.styleSheets.nextButtonSpan}>
                          arrow_right_alt
                        </span>
                      </Button>
                    </div>
                    <Button
                      hidden={this.state.backButtonState}
                      onClick={this.setPageBack}
                      style={this.styleSheets.backButton}
                    >
                      <span className="material-symbols-outlined rightArrow" style={this.styleSheets.backButtonSpan}>
                        arrow_left_alt
                      </span>
                    </Button>
                  </div>)}
              </div>






              <DeleteValidation DeleteValidation={this.setDeleteState} isDelete={this.state.isDelete} delete={this.removeCustomerFromDB} customer={this.state.selectedLog}></DeleteValidation>

              <div className='updateMain'>
                <Update
                  updateScreenState={this.setUpdateState}
                  isUpdate={this.state.isUpdate}
                  log={selectedLog}
                  getCustomers={this.getCustomers}
                  url={this.state.url}
                  paths={this.state.paths}
                ></Update>
              </div>





            </div>
            <Upload
              isUpload={this.state.isUpload}
              setUploadState={this.setUploadState}
              paths={this.state.paths}
              url={this.state.url}
              getLogs={this.getCustomers}
            ></Upload>

          </div>

        )}

      </div>
    )
  }
}
