import React, { Component } from 'react'
import { Button } from 'reactstrap'
import './css/update.css'
export default class update extends Component {


    state = {
        updateState: this.props.isUpdate,
        customerData: {},
        inserted: false,

    }


    componentDidMount() {
        document.addEventListener("keydown", (event) => {
            if (!this.props.isUpdate) {
                if (event.key === "Escape") this.props.updateScreenState(!this.props.isUpdate)
            }
        })
    }


    updateTexts = () => {



        const elements = document.querySelectorAll(".data")
        const inputs = document.querySelectorAll(".updateBoxes")

        let data = [];


        inputs.forEach(e => data.push(e.value))


        elements.forEach((e, index) => {
            console.log(data[index])
            e.innerText = data[index] !== "" ? data[index] : Object.values(this.props.customer)[index + 1]
        })
    }


    updateUser = async () => {
        const { url, paths } = this.props

        const elements = document.querySelectorAll(".updateBoxes")
        const { customer } = this.props
        const { customerData } = this.state
        let tempArray = {
            name: customerData.name,
            country: customerData.country,
            salary: customerData.salary,
            profession: customerData.profession
        }


        elements.forEach((e, index) => {


            if ((e.value.trim() !== "")) {


                let key = Object.keys(tempArray)[index];
                tempArray[key] = e.value


            }
        })


        this.setState({ inserted: !this.state.inserted })





        let options = {
            method: "POST",
            headers: {
                "Content-type": "Application/json"
            },
            body: JSON.stringify(tempArray)
        }

        let fetchUrl = url + paths.update + customer.id

        await fetch(fetchUrl, options).then(resp => resp.json()).then(resp => console.log(resp))

        this.updateTexts()
        this.props.getCustomers()
    }







    render() {



        const { updateScreenState, isUpdate, customer } = this.props


        return (




            <div
                className='mainContainer'
                hidden={isUpdate}
            >
                <span className='material-symbols-outlined icon'
                    onClick={() => {
                        updateScreenState(!isUpdate)
                    }}
                >close</span>
                <div className='updateContainer'>
                    <div className='customerData'>
                        {Object.keys(customer).map((e, index) => (
                            <p

                                style={{ fontWeight: 700 }}
                                key={index}
                            >{e.charAt(0).toUpperCase() + e.slice(1).toLowerCase() + " : "}<span className={index > 0 ? "data" : "id"} style={{ fontWeight: 500 }}>{Object.values(customer)[index]}</span></p>
                        ))}
                    </div>


                    <div className='updateInput'>
                        {Object.values(customer).map((e, index) => (
                            <input
                                key={index}
                                className={index > 0 ? 'updateBoxes' : "Id"}
                                style={{ outline: "none" }}
                                placeholder={e}
                            ></input>
                        ))}

                        <button
                            color={"dark"}
                            onClick={() => { this.updateUser(); }}
                        ><span className='material-symbols-outlined'>edit</span></button>
                    </div>

                </div>
            </div>
        )
    }
}
