import React, { Component} from 'react';
import {Table,Button} from 'reactstrap';
import './App.css';

import ListMethods from './components/listMethods';


export default class App extends Component {


  state = {
    customers : [],
    isUpdate : true,
    selectedUser : []
  }

  updateIsSetUpdate = (newState) => {
    this.setState({ isUpdate: newState });
  };


  sortCustomersBySalary = (sortParameter) =>{
    const { customers } = this.state;
    const sortedCustomers = [...customers]
    switch (sortParameter) {
      case 1 : {
        sortedCustomers.sort((a,b)=>a.id-b.id)
        this.setState({customers:sortedCustomers})
        break
      }
      case 2 : {
        sortedCustomers.sort((a, b) => {
          if (a.name < b.name) return -1;
          if (a.name > b.name) return 1;
          return 0;
        });
        this.setState({customers:sortedCustomers})
        break



      }
      case 3 : {
        sortedCustomers.sort((a, b) => {
          if (a.country < b.country) return -1;
          if (a.country > b.country) return 1;
          return 0;
        });
        this.setState({customers:sortedCustomers})
        break
      }
      case 4 :{
        sortedCustomers.sort((a,b)=>a.salary-b.salary)
        this.setState({customers:sortedCustomers})
        break
      }
      default : {
        break
      }
    }
  }

  updateCustomer= async () =>{
    const { selectedUser } = this.state


    const body =  {
      name : selectedUser.name,
      country : selectedUser.country,
      salary : selectedUser.salary,
      profession : selectedUser.profession 
    }

    const options = {
      method : "POST",
      headers : {
        "Content-type" : "Application/json"
      },
      body : JSON.stringify(body)
    }

   await fetch(`http://localhost:4444/update/${selectedUser.id}`,options)
   .then(resp=>resp.json())


  }
  getCustomers = async () =>{
    await fetch("http://localhost:4444/customers").then(resp=>resp.json()).then(resp=>this.setState({customers : resp}))
  }


  componentDidMount(){
    this.getCustomers()
  }

  setCurrentListMethod = method =>{
      this.setState({currentMethod : method});
  }

  setUpdateState = () =>{
    this.setState({ isUpdate: !this.state.isUpdate })
  }


  render() {
    const {customers,isUpdate,selectedUser} = this.state

    return (
      <div>
        {customers.length > 0 && (
          <div >
            <Table className='table'>
              <thead>
                <tr>
                  {Object.keys(customers[0]).map((e,index)=>(
                    <th key={index}>
                      {e}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {customers.map((e,index)=>(
                  <tr key={e.id}>
                    <td>{e.id}</td>
                    <td>{e.name}</td>
                    <td>{e.country}</td>
                    <td>{e.salary}</td>
                    <td>{e.profession}</td>
                    <td>
                      <Button
                        color='success'
                        style={{width:100}}
                        onClick={()=>{this.setUpdateState();console.log(isUpdate);this.setState({selectedUser : e})}}
                      >update</Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
            <ListMethods
              setCurrentListMethod={this.setCurrentListMethod}
              sortCustomers={this.sortCustomersBySalary}
            ></ListMethods>




          </div>


        )}

      </div>
    )
  }
}
