import React, { Component } from 'react'
import { Table, Button } from 'reactstrap'

export default class downloadSelected extends Component {

    state = {
        selectedStates: {}
    }




    styleSheets = {
        mainContainer: {
            width: "100vw",
            height: "100vh",
            position: "fixed",
            backgroundColor: "rgba(0,0,0,0.2)",
            zIndex: 2
        },
        layoutContainer: {
            width: "80vw",
            height: "90vh",
            position: "absolute",
            margin: "auto",
            inset: 0,
            backgroundColor: "#fff",
        },
        header: {
            width: "70vw",
            height: "8vh",
            backgroundColor: "#333",
            color: "#fff",
            position: "absolute",
            left: "5vw",
            top: "5vh",
            display: "flex",
            justifyContent: "center",
            alignItems: "center"

        },
        queryTable: {
            width: "70vw",
            height: "80vh"
        },
        query: {
            fontSize: 12,
            width: "max-content"
        },
        tableBody: {
            position: "absolute",
            height: "50vh",
            left: "5vw",
            top: "20vh",
            width: "70vw",
            overflow: "hidden",
            overflowY: "scroll",
            backgroundColor: ""
        },
        copyButton: {
            width: "10vw",
            height: 40,
            position: "absolute",
            left: "20vw",
            bottom: "5vh"
        },
        selectButton : {
            width : "40px",
            height : "40px",
            position : "relative",
            top : 25
            
        }
    }



    checkSelectedState = (entry,index) =>{
        this.state.selectedStates[`${index}`] = this.state.selectedStates[`${index}`]  === undefined && this.state.selectedStates[`${index}`] === null && this.state.selectedStates[`${index}`] === null
    }




    copyQueries = () => {
        const elements = document.querySelectorAll(".query")

        let queryText = ""


        elements.forEach((entry) => {
            queryText = queryText + "\n" + entry.innerText
        })


        navigator.clipboard.writeText(queryText).then(() => alert("Table copied successfully"))
    }




    render() {
        const { mainContainer, layoutContainer, header, queryTable, query, tableBody, copyButton , selectButton } = this.styleSheets
        const { isDownloadSelected, searchLogs } = this.props



        return (
            <div style={mainContainer} hidden={!isDownloadSelected}>
                <div style={layoutContainer}>
                    <div>
                        <Table style={queryTable}>
                            <thead>
                                <tr style={header}>
                                    <th>Query Codes</th>
                                </tr>
                            </thead>
                            <tbody style={tableBody}>
                                {searchLogs.length > 0 && (
                                    searchLogs.map((e, index) => (

                                        <tr key={index}>
                                            <td style={query} className="query">


                                                <span
                                                    className='material-symbols-outlined'
                                                    style={selectButton}
                                                    onClick={(a)=>{
                                                           
                                                        
                                                    }}
                                                >done</span><p style={{ width: "max-content", marginLeft: 40 }}><span style={{ color: "red" }}>insert into</span> [FORTUNA_CONSEPT].General.ResourceNewIB  <span style={{ color: "red" }}>values</span> ('{e.LanguageCode}','{e.PageName}','{e.ResourceCode}','{e.Resource}')</p>
                                            </td>
                                        </tr>



                                    ))
                                )

                                }
                            </tbody>
                        </Table>
                        <Button
                            style={copyButton}
                            onClick={this.copyQueries}
                            color="dark"
                        >Copy</Button>
                    </div>
                </div>
            </div>
        )
    }
}
