import React, { Component } from 'react';
import { Table, Button } from 'reactstrap';
import './App.css';


import Update from './components/update'
import ListMethods from './components/listMethods';




export default class App extends Component {


  state = {
    customers: [],
    isUpdate: true,
    selectedUser: [],
    notFound: true,
    isDelete: null,
    selectedUser: [],
    url: "http://localhost:4444",
    paths: {
      update: "/update/"
    }
  }



  searchCustomers = (input) => {
    if (input.length > 0) {
      const { customers } = this.state
      const unHandledCustomers = [...customers];
      const sortedCustomers = unHandledCustomers.filter((a) => a.name.toLowerCase().includes(input.toLowerCase()))
      if (sortedCustomers.length > 0) {
        this.setState({ customers: sortedCustomers })
        this.setState({ notFound: true })
      }
      else {
        this.setState({ notFound: false })
      }
    }
    else {
      this.getCustomers();
    }
  }

  removeCustomerFromDB = customer => {
    let newCustomers = [...this.state.customers]
    newCustomers = newCustomers.filter(e => e.id !== customer.id)
    this.setState({ customers: newCustomers })

    fetch(`http://localhost:4444/delete/${customer.id}`).then(resp => resp.json()).then(resp => console.log(resp.deleted))


  }



  sortCustomersBySalary = (sortParameter) => {
    const { customers } = this.state;
    const sortedCustomers = [...customers]
    switch (sortParameter) {
      case 1: {
        sortedCustomers.sort((a, b) => a.id - b.id)
        this.setState({ customers: sortedCustomers })
        break
      }
      case 2: {
        sortedCustomers.sort((a, b) => {
          if (a.name < b.name) return -1;
          if (a.name > b.name) return 1;
          return 0;
        });
        this.setState({ customers: sortedCustomers })
        break



      }
      case 3: {
        sortedCustomers.sort((a, b) => {
          if (a.country < b.country) return -1;
          if (a.country > b.country) return 1;
          return 0;
        });
        this.setState({ customers: sortedCustomers })
        break
      }
      case 4: {
        sortedCustomers.sort((a, b) => a.salary - b.salary)
        this.setState({ customers: sortedCustomers })
        break
      }
      case 5: {
        sortedCustomers.sort((a, b) => {
          if (a.profession < b.profession) return -1;
          if (a.profession > b.profession) return 1;
          return 0;
        });
        this.setState({ customers: sortedCustomers })
        break
      }
      default: {
        break
      }
    }
  }
  updateCustomer = async () => {
    const { selectedUser } = this.state


    const body = {
      name: selectedUser.name,
      country: selectedUser.country,
      salary: selectedUser.salary,
      profession: selectedUser.profession
    }

    const options = {
      method: "POST",
      headers: {
        "Content-type": "Application/json"
      },
      body: JSON.stringify(body)
    }

    await fetch(`http://localhost:4444/update/${selectedUser.id}`, options)
      .then(resp => resp.json())


  }
  getCustomers = async () => {
    await fetch("http://localhost:4444/customers").then(resp => resp.json()).then(resp => this.setState({ customers: resp }))
  }
  updateCustomer = customer => {

  }
  componentDidMount() {
    this.getCustomers()

  }
  setCurrentListMethod = element => {
    document.querySelectorAll(".listMethods").forEach(e => e.style.color = "#333")
    element.style.color = "rgb(29, 206, 41)"
  }
  setUpdateState = state => {

    this.setState({ isUpdate: state })
  }



  render() {
    const { customers, isUpdate, selectedUser } = this.state

    return (
      <div>
        {customers.length > 0 && (
          <div className='mainContainer_0'>
            <Table className='table'>
              <thead>
                <tr>
                  {Object.keys(customers[0]).map((e, index) => (
                    <th key={index} >
                      <h5
                        className={`listMethods lm-${index}`}
                        onClick={() => {
                          this.sortCustomersBySalary(index + 1);
                          this.setCurrentListMethod(document.querySelector(`.lm-${index}`))

                        }}


                      >{e}</h5>
                    </th>
                  ))}
                  <th style={{ textAlign: "center", width: 50 }}><h5>Edit</h5></th>
                  <th style={{ textAlign: "center", width: 50 }}><h5>Delete</h5></th>
                </tr>
              </thead>
              <tbody>
                {customers.map((e, index) => (
                  <tr key={e.id}>
                    <td>{e.id}</td>
                    <td>{e.name}</td>
                    <td>{e.country}</td>
                    <td>{e.salary}</td>
                    <td>{e.profession}</td>
                    <td>
                      <Button
                        color='dark'
                        style={{ width: 50, margin: 0, marginLeft: 10 }}
                        onClick={() => {
                          this.setUpdateState(!isUpdate);
                          this.setState({ selectedUser: e });

                        }}
                      ><span className='material-symbols-outlined'>edit</span></Button>
                    </td>
                    <td>
                      <Button
                        color='danger'
                        style={{ width: 50, margin: 0, marginRight: 10 }}
                        onClick={() => {
                          this.removeCustomerFromDB(e)
                        }}
                      ><span className='material-symbols-outlined'>delete</span></Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
            <ListMethods
              setCurrentListMethod={this.setCurrentListMethod}
              sortCustomers={this.sortCustomersBySalary}
              searchCustomers={this.searchCustomers}
              notFound={this.state.notFound}
            ></ListMethods>
            <div
              className='updateMain'
            >
              <Update updateScreenState={this.setUpdateState} isUpdate={this.state.isUpdate} customer={selectedUser} getCustomers={this.getCustomers} url={this.state.url} paths={this.state.paths}></Update>
            </div>




          </div>


        )}

      </div>
    )
  }
}
