import React, { Component } from 'react'
import { Button } from 'reactstrap'
import './css/update.css'
export default class update extends Component {
    

    state = {
        updateState: this.props.isUpdate,
        customerData : {
            id : Object.values(this.props.customer)[0],
            name : Object.values(this.props.customer)[1],
            country : Object.values(this.props.customer)[2],
            salary : Object.values(this.props.customer)[3],
            profession : Object.values(this.props.customer)[4]
        }
    }


    componentDidMount() {

        document.addEventListener("keydown", (event) => {
            if (!this.props.isUpdate) {
                if (event.key === "Escape") this.props.updateScreenState(!this.props.isUpdate)
            }
        })
    }



    updateUser = () =>{
        const elements = document.querySelectorAll(".updateBoxes")
        const { customer } = this.props
        const { customerData } =this.state
        
        
        elements.forEach((e,index)=>{
            
            
            if((e.value.trim() !=="")){
            

                let key = Object.keys(customerData)[index + 1];
                customerData[key] = e.value
               
                
            }
        })



        
        
        let options = {
            method : "POST",
            headers : {
                "Content-type" : "Application/json"
            },
            body : JSON.stringify(tempArray)
        }

        fetch(`http://localhost:4444/update/${customer.id}`,options).then(resp=>resp.json()).then(resp=>console.log(resp))

        
    }







    render() {



        const { updateScreenState, isUpdate, customer } = this.props

        return (




            <div
                className='mainContainer'
                hidden={isUpdate}
            >
                <span className='material-symbols-outlined icon'
                    onClick={() => {
                        updateScreenState(!isUpdate)
                    }}
                >close</span>
                <div className='updateContainer'>
                    <div className='customerData'>
                        {Object.keys(customer).map((e, index) => (
                            <p
                                style={{ fontWeight: 700 }}
                                key={index}
                            >{e.charAt(0).toUpperCase() + e.slice(1).toLowerCase() + " : "}<span style={{ fontWeight: 500 }}>{Object.values(customer)[index]}</span></p>
                        ))}
                    </div>


                    <div className='updateInput'>
                        {Object.values(customer).map((e, index) => (
                            <input
                                key={index}
                                className={index > 0 ? 'updateBoxes' : "Id"}
                                style={{ outline: "none" }}
                                placeholder={e}
                            ></input>
                        ))}

                        <Button
                            color={"dark"}
                            onClick={this.updateUser}
                        ><span className='material-symbols-outlined'>edit</span></Button>
                    </div>

                </div>
            </div>
        )
    }
}
