import React, { Component } from 'react'
import { Input, InputGroupText } from 'reactstrap'

export default class insertSingle extends Component {

    styleSheets = {
        main: {
            width: "100vw",
            height: "100vh",
            backgroundColor: "rgba(0,0,0,0.2)",
            position: "fixed",
            zIndex: 2
        },
        container: {
            width: 600,
            height: 400,
            backgroundColor: "#fff",
            position: "absolute",
            inset: 0,
            margin: "auto"
        },
        closeButton: {
            background: "transparent",
            border: "none",
            width: 30,
            height: 30,
            color: "#fff",
            position: "absolute",
            top: 30,
            right: 50
        }
    }


    render() {
        const { main, container, closeButton } = this.styleSheets
        const { isInsert, setInsertState } = this.props


        return (
            <div style={main} hidden={!isInsert}>
                <button
                    style={closeButton}
                    onClick={()=>{
                        setInsertState(!isInsert)
                    }} 
                ><span className='material-symbols-outlined'>close</span></button>
                <div style={container}>
                    <Input
                        
                    ><InputGroupText>Label</InputGroupText></Input>


                </div>
            </div>
        )
    }
}
