import React, { Component } from 'react'
import Search from './searcher.js'
import './css/listMethods.css'
import {
  ListGroup,
  ListGroupItem,
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
} from 'reactstrap';

export default class listMethods extends Component {

  state = {
    customers: this.props.customers,
    isActive: false,
    listMethods: ["List by id", "List by name", "List by country", "List by salary"],
    currentMethod: "0",
    isUpdate: true,
    selectedUser: [],
    collapsed : false
  }

 

   toggleNavbar = () => this.setState({collapsed:!this.state.collapsed});


  render() {
    const { setCurrentListMethod, sortCustomers } = this.props
    const { listMethods } = this.state
    return (
      <div>
         <ListGroup className='listBar' hidden={true}>
        <h2 style={{ textAlign: "center", padding: 25 }}>List Methods</h2>
        {listMethods.map((e, index) => (
          <ListGroupItem
            tag="a"
            className='listSection'
            key={index}
            onClick={() => { setCurrentListMethod(index); sortCustomers(index + 1) }}
            active={this.state.currentMethod === index ? true : false}
          >{e}</ListGroupItem>
        ))}


        
      </ListGroup>



        <div className='navi'>
          <Navbar color="faded" light>
            <NavbarBrand href="/" className="me-auto">
              reactstrap
            </NavbarBrand>
            <NavbarToggler onClick={this.toggleNavbar} className="me-2" />
            <Collapse isOpen={!this.state.collapsed} navbar>
              <Nav navbar>
                {listMethods.map((e,index)=>(
                  <NavItem
                   key={index}
                   className='listSection'
                   onClick={() => { setCurrentListMethod(index); sortCustomers(index + 1) }}
                   active={this.state.currentMethod === index ? true : false}
                  >
                    <NavLink></NavLink>
                  </NavItem>
                ))}
              </Nav>
            </Collapse>
          </Navbar>
        </div>

        <div>
          <Search notFound={this.props.notFound} searchCustomers={this.props.searchCustomers}></Search>
        </div>

      </div>
     
    )
  }
}
