import React, { Component } from 'react';
import { Table, Button } from 'reactstrap';
import './App.css';


import DeleteValidation from './components/deleteValidation'
import Update from './components/update'
import Searcher from './components/searcher'





export default class App extends Component {


  state = {
    logs: [],
    isUpdate: true,
    notFound: true,
    isDelete: false,
    selectedLog: [],
    url: "http://localhost:4444",
    paths: {
      update: "/update/",
      delete: "/delete/",
      getAllCustomers: "/logs"
    },
    page: 1,
    currentPageLogs: []
  }



  searchCustomers = (input, sortParam) => {
    if (input.length > 0) {
      const { logs } = this.state
      const unHandledLogs = [...logs];
      let sortedLogs = []
      switch (sortParam) {
        case 0: {

          sortedLogs = unHandledLogs.filter((a) => `${a.Id}`.includes(input))
          break;
        }
        case 1: {
          sortedLogs = unHandledLogs.filter((a) => a.LanguageCode.toLowerCase().includes(input.toLowerCase()))
          break;
        }
        case 2: {
          sortedLogs = unHandledLogs.filter((a) => a.PageName.toLowerCase().includes(input.toLowerCase()))
          break;
        }
        case 3: {
          sortedLogs = unHandledLogs.filter((a) => a.ResourceCode.toLowerCase().includes(input.toLowerCase()))
          break;
        }
        case 4: {
          sortedLogs = unHandledLogs.filter((a) => a.Resource.toLowerCase().includes(input.toLowerCase()))
          break;
        }
      }
      if (sortedLogs.length > 0) {
        this.setState({ logs: sortedLogs })
        this.setState({ notFound: true })
      }
      else {
        this.getCustomers()
        this.setState({ notFound: false })
      }
    }
    else {
      this.getCustomers();
    }
  }



  // deleteValidation = (user) =>{
  //   const result = confirm("Are you sure you want to delete");
  //   if(result) this.removeCustomerFromDB(user)
  // }


  removeCustomerFromDB = customer => {
    const { url, paths, logs } = this.state
    let newCustomers = [...logs]
    newCustomers = newCustomers.filter(e => e.Id !== customer.Id)
    this.setState({ logs: newCustomers })
    let fetchUrl = url + paths.delete + customer.Id
    fetch(fetchUrl).then(resp => resp.json()).then(resp => console.log(resp.deleted))
  }

  setDeleteState = state => {
    this.setState({ isDelete: state })
  }

  sortCustomersByResourceCode = (sortParameter) => {
    const { logs } = this.state;
    const sortedCustomers = [...logs]
    switch (sortParameter) {
      case 1: {
        sortedCustomers.sort((a, b) => a.Id - b.Id)
        this.setState({ logs: sortedCustomers })
        break
      }
      case 2: {
        sortedCustomers.sort((a, b) => {
          if (a.LanguageCode < b.LanguageCode) return -1;
          if (a.LanguageCode > b.LanguageCode) return 1;
          return 0;
        });
        this.setState({ logs: sortedCustomers })
        break



      }
      case 3: {
        sortedCustomers.sort((a, b) => {
          if (a.PageName < b.PageName) return -1;
          if (a.PageName > b.PageName) return 1;
          return 0;
        });
        this.setState({ logs: sortedCustomers })
        break
      }
      case 4: {
        sortedCustomers.sort((a, b) => a.ResourceCode - b.ResourceCode)
        this.setState({ logs: sortedCustomers })
        break
      }
      case 5: {
        sortedCustomers.sort((a, b) => {
          if (a.Resource < b.Resource) return -1;
          if (a.Resource > b.Resource) return 1;
          return 0;
        });
        this.setState({ logs: sortedCustomers })
        break
      }
      default: {
        break
      }
    }
  }


  getCustomers = async () => {
    const { url, paths } = this.state
    let fetchUrl = url + paths.getAllCustomers
    await fetch(fetchUrl).then(resp => resp.json()).then(resp => this.setState({ logs: resp }))

  }

  async componentDidMount() {
    await this.getCustomers()
    await this.putLogsInTable();
  }
  setCurrentListMethod = element => {
    document.querySelectorAll(".listMethods").forEach(e => e.style.color = "#333")
    element.style.color = "rgb(29, 206, 41)"
  }
  setUpdateState = state => {

    this.setState({ isUpdate: state })
  }


  putLogsInTable = () => {
    const { page, logs } = this.state;
    let tempArray = [];

    for (var i = 0; i < page * 10; i++) {
      if (Object.values(logs)[i] !== undefined) {
        tempArray.push(Object.values(logs)[i]);
      }
    }

    this.setState({ currentPageLogs: tempArray });
  };


  render() {
    const { logs, isUpdate, selectedLog, currentPageLogs } = this.state

    return (
      <div>
        {logs.length > 0 && (
          <div>
            <Searcher searchCustomers={this.searchCustomers} logs={this.state.logs} notFound={this.state.notFound}></Searcher>
            <div className='mainContainer'>

              <div
                className='table'
              >
                <Table>
                  <thead>
                    <tr>
                      {Object.keys(logs[0]).map((e, index) => (
                        <th key={index} className="headerCol">
                          <h5
                            className={`listMethods lm-${index}`}
                            onClick={() => {
                              this.sortCustomersByResourceCode(index + 1);
                              this.setCurrentListMethod(document.querySelector(`.lm-${index}`))

                            }}


                          >{e}</h5>
                        </th>
                      ))}
                      <th style={{ textAlign: "center", wIdth: 50, position: "relative", left: "30px" }}><h5>Edit</h5></th>
                      <th style={{ textAlign: "center", wIdth: 50 }}><h5>Delete</h5></th>
                    </tr>
                  </thead>
                  <tbody>
                    {logs.map((e, index) => (
                      <tr key={e.Id} className='logData'>
                        <td>{e.Id}</td>
                        <td>{e.LanguageCode}</td>
                        <td>{e.PageName}</td>
                        <td>{e.ResourceCode}</td>
                        <td>{e.Resource}</td>
                        <td style={{ width: "3vw", position: "relative", left: "30px" }}>
                          <Button
                            color='dark'
                            style={{  marginLeft: 20 }}
                            onClick={() => {
                              this.setUpdateState(!isUpdate);
                              this.setState({ selectedLog: e });

                            }}
                          ><span className='material-symbols-outlined'>edit</span></Button>
                        </td>
                        <td style={{ width: "3vw" }}>
                          <Button
                            color='danger'
                            style={{  marginLeft:0, }}
                            onClick={() => {
                              this.setState({ isDelete: !this.state.isDelete })
                              this.setState({ selectedLog: e })
                            }}
                          ><span className='material-symbols-outlined'>delete</span></Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </div>






              <DeleteValidation DeleteValidation={this.setDeleteState} isDelete={this.state.isDelete} delete={this.removeCustomerFromDB} customer={this.state.selectedLog}></DeleteValidation>

              <div className='updateMain'>
                <Update
                  updateScreenState={this.setUpdateState}
                  isUpdate={this.state.isUpdate}
                  log={selectedLog}
                  getCustomers={this.getCustomers}
                  url={this.state.url}
                  paths={this.state.paths}
                ></Update>
              </div>





            </div>


          </div>

        )}

      </div>
    )
  }
}
