import React, { Component } from "react";
import { FormGroup, Input, Table } from "reactstrap";
import * as XLSX from 'xlsx';
import './css/upload.css';

export default class Upload extends Component {
    state = {
        xlsxData: [],
        fileName: ''
    };

    handleFile = (event) => {
        const file = event.target.files[0];
        if(file.name.includes(".xlsx"))this.convertExcelToJson(file);
        else{
            alert("Sadece xlsx uzantılı dosyalar listelenir")
        }
        
    };

    clearInput = () =>{
        document.querySelector(".fileInput").value = ""
        this.setState({xlsxData: []})
    }


    convertExcelToJson = (file) => {


       try{
        const reader = new FileReader();


        reader.onload = (event) => {
            let data = event.target.result;
            let workbook = XLSX.read(data, { type: 'binary' });
            let tempData = [];
            workbook.SheetNames.forEach((entry) => {
                let worksheet = workbook.Sheets[entry];
                let xlsxData = XLSX.utils.sheet_to_json(worksheet, { raw: false });
                tempData.push(xlsxData);
                console.log(xlsxData);
            });
            this.setState({ xlsxData: tempData[0] });
        };
        reader.readAsBinaryString(file);
       }
       catch(err){
        alert("Yüklenen dosya xlsx olmalı")
       }
    };

    render() {
        const { fileName, xlsxData } = this.state;

        return (
            <div className="uploadMain" hidden={this.props.isUpload}>
                <span
                    className="material-symbols-outlined close"
                    onClick={() => { this.props.setUploadState(!this.props.isUpload);this.clearInput() }}
                >
                    close
                </span>
                <div className="uploadContainer">
                    <FormGroup>
                        <Input
                            className="fileInput"
                            type="file"
                            id="fileInput"
                            onChange={this.handleFile}
                            accept=".xlsx"
                        />
                    </FormGroup>
                    {xlsxData.length > 0 && (
                        <div className="previewTable">
                            <Table>
                                <thead>
                                    <tr>
                                        {Object.keys(xlsxData[0]).map((e, index) => (
                                            <th key={index} className="xlsxTableHeaders">{e}</th>
                                        ))}
                                    </tr>
                                </thead>
                                <tbody>
                                    {Object.values(xlsxData).map((e, index) => (
                                        <tr key={index}>
                                            {Object.values(e).map((j, key) => (<td key={key}>{j}</td>))}
                                        </tr>
                                    ))}
                                </tbody>
                            </Table>
                        </div>
                    )}
                </div>
            </div>
        );
    }
}
